import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const navigate = useNavigate();
  const storedUser = localStorage.getItem("user");
  let user = null;

  try {
    user = storedUser ? JSON.parse(storedUser) : null;
  } catch (error) {
    console.error("Error parsing user data", error);
  }

  const [formData, setFormData] = useState({
    name: "",
    lastname: "",
    age: "",
    address: "",
    tel: "",
  });
  const [isEditing, setIsEditing] = useState(false);  // ใช้เพื่อเช็คว่ากำลังแก้ไขหรือไม่

  useEffect(() => {
    if (!user) {
      navigate("/login");
      return;
    }

    fetch(`http://localhost:5001/api/user/${user.username}`)
      .then((res) => res.json())
      .then((data) => setFormData(data))
      .catch((error) => console.error("Error fetching user data", error));
  }, [user, navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch(`http://localhost:5001/api/user/${user.username}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    })
      .then((res) => res.json())
      .then((data) => {
        alert(data.message);
        setIsEditing(false); // เมื่ออัปเดตเสร็จให้กลับมาแสดงเป็นข้อความ
      })
      .catch((error) => console.error("Error updating profile", error));
  };

  return (
    <div>
      <center>
        <h1>Profile</h1>
        {isEditing ? (
          // เมื่อกำลังแก้ไขจะแสดงฟอร์ม
          <form onSubmit={handleSubmit}>
            <label>
              Name: <input type="text" name="name" value={formData.name} onChange={handleChange} required />
            </label>
            <br />
            <label>
              Lastname: <input type="text" name="lastname" value={formData.lastname} onChange={handleChange} required />
            </label>
            <br />
            <label>
              Age: <input type="number" name="age" value={formData.age} onChange={handleChange} required />
            </label>
            <br />
            <label>
              Address: <input type="text" name="address" value={formData.address} onChange={handleChange} required />
            </label>
            <br />
            <label>
              Tel: <input type="text" name="tel" value={formData.tel} onChange={handleChange} required />
            </label>
            <br />
            <button type="submit">Update Profile</button>
          </form>
        ) : (
          // เมื่อไม่กำลังแก้ไขจะแสดงเป็นข้อความ
          <div>
            <p><strong>Name:</strong> {formData.name}</p>
            <p><strong>Lastname:</strong> {formData.lastname}</p>
            <p><strong>Age:</strong> {formData.age}</p>
            <p><strong>Address:</strong> {formData.address}</p>
            <p><strong>Tel:</strong> {formData.tel}</p>
            <button onClick={() => setIsEditing(true)}>Edit</button>
          </div>
        )}
        <button onClick={() => navigate(`/${user.role}`)}>Back to Dashboard</button>
      </center>
    </div>
  );
};

export default Profile;
