// client/src/context/AuthContext.js
import React, { createContext, useState } from 'react';
import { loginApi } from '../service/api';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);   // เก็บข้อมูล user { username, role }
  const [token, setToken] = useState('');   // เก็บ token (จำลอง)
  const [error, setError] = useState('');

  const login = async (username, password, callback) => {
    // เรียก API ไปที่ server
    const data = await loginApi(username, password);

    if (!data.success) {
      setError(data.message || 'Login failed');
      return;
    }

    // ถ้า success -> set user, token
    setUser(data.user);    // { username, role }
    setToken(data.token);  // token
    setError('');

    // ในระบบจริง อาจเก็บ token ลง localStorage
    // localStorage.setItem('token', data.token);

    callback();  // กลับไปแจ้งให้หน้า Login ทำงานต่อ (navigate)
  };

  const logout = (callback) => {
    setUser(null);
    setToken('');
    setError('');
    // localStorage.removeItem('token');
    callback();
  };

  return (
    <AuthContext.Provider value={{ user, token, error, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
