    })
      .then((res) => res.json())
      .then((data) => {
        alert(data.message);
        setIsEditing(false); // เมื่ออัปเดตเสร็จให้กลับมาแสดงเป็นข้อความ
      })
      .catch((error) => console.error("Error updating profile", error));
  };

  return (
    <div>
      <center>
        <h1>Profile</h1>
        {isEditing ? (
          // เมื่อกำลังแก้ไขจะแสดงฟอร์ม
          <form onSubmit={handleSubmit}>
            <label>
              Name: 
              <input 
                type="text" 
                name="name" 
                value={name}  // เชื่อมโยงกับ formData
                onChange={(e) => setName(e.target)}
                required 
              />
            </label>
            <br />
            <label>
              Lastname: 
              <input 
                type="text" 
                name="lastname" 
                value={lastName} 
                onChange={(e) => setLastName(e.target)}
                required 
              />
            </label>
            <br />
            <label>
              Age: 
              <input 
                type="number" 
                name="age" 
                value={age} 
                onChange={(e) => setAge(e.target)}
                required 
              />
            </label>
            <br />
            <label>
              Address: 
              <input 
                type="text" 
                name="address" 
                value={address} 
                onChange={(e) => setAddress(e.target)}
                required 
              />
            </label>
            <br />
            <label>
              Tel: 
              <input 
                type="text" 
                name="tel" 
                value={tel} 
                onChange={(e) => setTel(e.target)}
                required 
              />
            </label>
            <br />
            <button type="submit">Update Profile</button>
          </form>
        ) : (
          // เมื่อไม่กำลังแก้ไขจะแสดงเป็นข้อความ
          <div>
            <p><strong>Name:</strong> {formData.name}</p>
            <p><strong>Lastname:</strong> {formData.lastname}</p>
            <p><strong>Age:</strong> {formData.age}</p>
            <p><strong>Address:</strong> {formData.address}</p>
            <p><strong>Tel:</strong> {formData.tel}</p>
            <button onClick={() => setIsEditing(true)}>Edit</button>
          </div>
        )}
        <button onClick={() => navigate(`/${user.role}`)}>Back to Dashboard</button>
      </center>
    </div>
  );
};

export default Profile;
