import { Routes, Route } from "react-router-dom"; // ✅ ถูกต้อง
import Login from "./pages/Login";
import Register from "./pages/Register";
import AdminDashboard from "./pages/AdminDashboard";
import UserDashboard from "./pages/UserDashboard";
import ManagerDashboard from "./pages/ManagerDashboard";

function App() {
  return (
    <Router> {/* ✅ ครอบ Routes ด้วย Router */}
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/admin" element={<AdminDashboard />} />  {/* ✅ ใช้ /admin แทน /AdminDashboard */}
        <Route path="/user" element={<UserDashboard />} />  {/* ✅ ใช้ /user แทน /UserDashboard */}
        <Route path="/manager" element={<ManagerDashboard />} />  {/* ✅ ใช้ /manager แทน /ManagerDashboard */}
      </Routes>
    </Router>
  );
}

export default App;
